function subst() {
var vars = {};
var x = document.location.search.substring(1).split('&');
for (var i in x) {
var z = x[i].split('=', 2);
vars[z[0]] = unescape(z[1]);
}
var currPage = parseInt(vars['page']);

// 1. Hide main header on Cover Page (Page 1)
var companyInfo = document.getElementById("company_header_info");
if(companyInfo) {
companyInfo.style.display = (currPage == 1) ? 'none' : 'block';
}

// 2. Show Table of Contents text ONLY on Page 2
var tocHeader = document.getElementById("toc_header_info");
if (tocHeader) {
tocHeader.style.display = (currPage == 2) ? 'block' : 'none';
}

// 3. Show Entity Information text ONLY on Page 3
var entityHeader = document.getElementById("entity_header_info");
if (entityHeader) {
entityHeader.style.display = (currPage == 3) ? 'block' : 'none';
}

// 4. Show Report of Directors on Pages 4, 5, and 6
var directorsHeader = document.getElementById("directors_header_info");
if (directorsHeader) {
directorsHeader.style.display = (currPage >= 4 && currPage <= 6) ? 'block' : 'none';
}

// 5. Show Independent Auditor Report on Pages 7, 8, and 9
var auditorHeader = document.getElementById("auditor_header_info");
if (auditorHeader) {
auditorHeader.style.display = (currPage >= 7 && currPage <= 9) ? 'block' : 'none';
}

// 6. Show Statement of Financial Position ONLY on Page 10
var positionHeader = document.getElementById("financial_position_header_info");
if (positionHeader) {
positionHeader.style.display = (currPage == 10) ? 'block' : 'none';
}

// 7. Show Statement of Profit and Loss ONLY on Page 11
var profitLossHeader = document.getElementById("profit_loss_header_info");
if (profitLossHeader) {
profitLossHeader.style.display = (currPage == 11) ? 'block' : 'none';
}

// 8. Show Statement of Changes in Equity ONLY on Page 12
var equityHeader = document.getElementById("equity_header_info");
if (equityHeader) {
equityHeader.style.display = (currPage == 12) ? 'block' : 'none';
}

// 9. NEW: Show Statement of Cash Flows ONLY on Page 13
var cashFlowHeader = document.getElementById("cash_flow_header_info");
if (cashFlowHeader) {
cashFlowHeader.style.display = (currPage == 13) ? 'block' : 'none';
}

// 10. Show Notes text from Page 14 onwards
var notesStartPage = 14;
var notesHeader = document.getElementById("notes_header_info");
if (notesHeader) {
notesHeader.style.display = (currPage >= notesStartPage) ? 'block' : 'none';
}
}
window.onload = subst;

(function() {
var vars = {};
var x = document.location.search.substring(1).split('&');
for (var i in x) {
var z = x[i].split('=', 2);
vars[z[0]] = unescape(z[1]);
}
var page = parseInt(vars['page']);
var footer = document.getElementById('custom_footer_content');
if (footer) {
if (page >= 3) {
footer.innerHTML = 'Page ' + (page - 2);
} else {
footer.style.display = 'none';
}
}
})();
